INSERT INTO `song_1815` (`song_number_1815`, `title`, `display`, `link`, `content`) VALUES
(1, 'O du fröhliche, o du selige', 'O du fröhliche, o du selige, Johannes Falk (1815)', 'odufroehlicheoduselige', '<p>\r\nO du fröhliche, o du selige,<br>\r\ngnadenbringende Weihnachtszeit!<br>\r\nWelt ging verloren, Christ ist geboren:<br>\r\nFreue, freue dich, o Christenheit!<br>\r\n</p>\r\n<p>\r\nO du fröhliche, o du selige,<br>\r\ngnadenbringende Weihnachtszeit!<br>\r\nChrist ist erschienen, uns zu versühnen:<br>\r\nFreue, freue dich, o Christenheit!<br>\r\n</p>\r\n<p>\r\nO du fröhliche, o du selige,<br>\r\ngnadenbringende Weihnachtszeit!<br>\r\nHimmlische Heere jauchzen Dir Ehre:<br>\r\nFreue, freue dich, o Christenheit!  <br>                \r\n</p>\r\n'),
(2, 'Stille Nacht, heilige Nacht', 'Stille Nacht, heilige Nacht (1818)', 'stillenachtheiligenacht', '<p>\r\nStille Nacht, heilige Nacht!<br>\r\nAlles schläft, einsam wacht<br>\r\nNur das traute, hochheilige Paar.<br>\r\nHolder Knabe im lockigen Haar,<br>\r\nSchlaf in himmlischer Ruh,<br>\r\nSchlaf in himmlischer Ruh!<br>\r\n</p>\r\n<p>\r\nStille Nacht, Heilige Nacht,<br>\r\nHirten erst kundgemacht.<br>\r\nDurch der Engel Halleluja<br>\r\nTönt es laut von fern und nah:<br>\r\nChrist, der Retter ist da,<br>\r\nChrist, der Retter ist da!<br>                    \r\n</p>\r\n<p>\r\nStille Nacht, heilige Nacht!<br>\r\nGottes Sohn, o wie lacht<br>\r\nLieb\' aus deinem göttlichen Mund,<br>\r\nDa uns schlägt die rettende Stund\',<br>\r\nChrist, in deiner Geburt,<br>\r\nChrist, in deiner Geburt.<br>                    \r\n</p>\r\n'),
(3, 'Ich hab mich ergeben', 'Ich hab mich ergeben (1820)', 'ichhabmichergeben', '<p>\r\nIch hab mich ergeben<br>\r\nmit Herz und mit Hand<br>\r\ndir, Land voll Lieb’ und Leben,<br>\r\nmein deutsches Vaterland!<br>\r\n</p>\r\n<p>\r\nMein Herz ist entglommen,<br>\r\ndir treu zugewandt,<br>\r\ndu Land der Frei’n und Frommen,<br>\r\ndu herrlich Hermannsland!<br>                      \r\n</p>\r\n<p>\r\nDu Land, reich an Ruhme,<br>\r\nwo Luther erstand,<br>\r\nfür deines Volkes Tume<br>\r\nreich ich mein Herz und Hand!<br>                    \r\n</p>\r\n<p>\r\nWill halten und glauben<br>\r\nan Gott fromm und frei,<br>\r\nwill, Vaterland, dir bleiben<br>\r\nauf ewig fest und treu.<br>                     \r\n</p>\r\n<p>\r\nAch Gott, tu erheben<br>\r\nmein jung Herzensblut<br>\r\nzu frischem freud’gen Leben,<br>\r\nzu freiem frommen Mut!<br>                      \r\n</p>\r\n<p>\r\nLass Kraft mich erwerben<br>\r\nin Herz und in Hand,<br>\r\nzu leben und zu sterben<br>\r\nfürs heil’ge Vaterland!<br>                      \r\n</p>\r\n'),
(4, 'Die Loreley', 'Die Loreley, Heinrich Heine (1824)', 'dieloreley', '<p>\r\nIch weiß nicht, was soll es bedeuten,<br>\r\ndass ich so traurig bin;<br>\r\nein Märchen aus alten Zeiten,<br>\r\ndas kommt mir nicht aus dem Sinn.<br>\r\n</p>\r\n<p>\r\nDie Luft ist kühl und es dunkelt,<br>\r\nund ruhig fließt der Rhein;<br>\r\nder Gipfel des Berges funkelt<br>\r\nim Abendsonnenschein.<br>\r\n</p>\r\n<p>\r\nDie schönste Jungfrau sitzet<br>\r\ndort oben wunderbar;<br>\r\nihr goldnes Geschmeide blitzet,<br>\r\nsie kämmt ihr goldenes Haar.<br>\r\n</p>\r\n<p>\r\nSie kämmt es mit goldenem Kamme<br>\r\nund singt ein Lied dabei;<br>\r\ndas hat eine wundersame,<br>\r\ngewaltige Melodei.<br>\r\n</p>\r\n<p>\r\nDen Schiffer im kleinen Schiffe<br>\r\nergreift es mit wildem Weh;<br>\r\ner schaut nicht die Felsenriffe,<br>\r\ner schaut nur hinauf in die Höh.<br>\r\n</p>\r\n<p>\r\nIch glaube, die Wellen verschlingen<br>\r\nam Ende Schiffer und Kahn;<br>\r\nund das hat mit ihrem Singen<br>\r\ndie Loreley getan.<br>\r\n</p>\r\n'),
(5, 'O Tannenbaum', 'O Tannenbaum, Ernst Anschütz (1824)', 'otannenbaum', '<p>\r\nO Tannenbaum, o Tannenbaum,<br>\r\nWie grün sind deine Blätter!<br>\r\nDu grünst nicht nur zur Sommerzeit,<br>\r\nNein, auch im Winter, wenn es schneit.<br>\r\nO Tannenbaum, o Tannenbaum,<br>\r\nWie treu sind deine Blätter!<br>\r\n</p>\r\n<p>\r\nO Tannenbaum, o Tannenbaum,<br>\r\nDu kannst mir sehr gefallen!<br>\r\nWie oft hat nicht zur Weihnachtszeit<br>\r\nEin Baum von dir mich hoch erfreut!<br>\r\nO Tannenbaum, o Tannenbaum,<br>\r\nDu kannst mir sehr gefallen!  <br>                      \r\n</p>\r\n<p>\r\nO Tannenbaum, o Tannenbaum,<br>\r\nDein Kleid will mich was lehren:<br>\r\nDie Hoffnung und Beständigkeit<br>\r\nGibt Mut und Kraft zu jeder Zeit!<br>\r\nO Tannenbaum, o Tannenbaum,<br>\r\nDein Kleid will mich was lehren! <br>                       \r\n</p>\r\n'),
(6, 'An der Saale hellem Strande', 'An der Saale hellem Strande, Franz Kugler (1826)', 'andersaalehellemstrande', '<p>\r\nAn der Saale hellem Strande<br>\r\nStehen Burgen stolz und kühn.<br>\r\nIhre Dächer sind gefallen,<br>\r\nUnd der Wind streicht durch die Hallen,<br>\r\nWolken ziehen drüber hin.    <br>                    \r\n</p>\r\n<p>\r\nZwar die Ritter sind verschwunden,<br>\r\nNimmer klingen Speer und Schild;<br>\r\nDoch dem Wandersmann erscheinen<br>\r\nIn den altbemoosten Steinen<br>\r\nOft Gestalten zart und mild.<br>\r\n</p>\r\n<p>\r\nDroben winken holde Augen,<br>\r\nFreundlich lacht manch roter Mund.<br>\r\nWandrer schauet in die Ferne,<br>\r\nSchaut in holder Augen Sterne,<br>\r\nHerz ist heiter und gesund.<br>\r\n</p>\r\n<p>\r\nUnd der Wandrer zieht von dannen,<br>\r\nDenn die Trennungsstunde ruft;<br>\r\nUnd er singet Abschiedslieder,<br>\r\nLebewohl tönt ihm hernieder,<br>\r\nTücher wehen in der Luft.<br>\r\n</p>\r\n'),
(7, 'Muss i denn', 'Muss i denn, Friedrich Silcher (1827)', 'mussidenn', '<p>\r\nMuss i denn, muss i denn<br>\r\nzum Städtele hinaus, Städtele hinaus,<br>\r\nUnd du, mein Schatz, bleibst hier?<br>\r\nWenn i komm’, wenn i komm’,<br>\r\nwenn i wiedrum komm’,<br>\r\nKehr’ i ein, mein Schatz, bei dir.<br>\r\nKann i glei net allweil bei dir sein,<br>\r\nHan i doch mein Freud’ an dir!<br>\r\nWenn i komm’, wenn i komm’,<br>\r\nwenn i wiedrum komm’,<br>\r\nKehr’ i ein, mein Schatz, bei dir.<br>\r\n</p>\r\n<p>\r\nWie du weinst, wie du weinst,<br>\r\nDass i wandere muss, wandere muss,<br>\r\nWie wenn d’ Lieb’ jetzt wär’ vorbei!<br>\r\nSind au drauss, sind au drauss<br>\r\nDer Mädele viel, Mädele viel,<br>\r\nLieber Schatz, i bleib dir treu.<br>\r\nDenk du net, wenn i ’ne Andre seh’,<br>\r\nNo sei mein’ Lieb’ vorbei;<br>\r\nSind au drauss, sind au drauss<br>\r\nDer Mädele viel, Mädele viel,<br>\r\nLieber Schatz, i bleib dir treu.<br>\r\n</p>\r\n<p>\r\nÜber’s Jahr, über’s Jahr,<br>\r\nWenn me Träubele schneid’t, Träubele schneid’t,<br>\r\nStell’ i hier mi wiedrum ein;<br>\r\nBin i dann, bin i dann<br>\r\nDein Schätzele noch, Schätzele noch,<br>\r\nSo soll die Hochzeit sein.<br>\r\nÜber’s Jahr, do ist mein’ Zeit vorbei,<br>\r\nDa g’hör’ i mein und dein;<br>\r\nBin i dann, bin i dann<br>\r\nDein Schätzele noch, Schätzele noch,<br>\r\nSo soll die Hochzeit sein.<br>\r\n</p>\r\n'),
(8, 'Gold und Silber lieb ich sehr', 'Gold und Silber lieb ich sehr, August Schnezler (1828)', 'goldundsilberliebichsehr', '<p>\r\nGold und Silber lieb\' ich sehr,<br>\r\nKann\'s auch gut gebrauchen,<br>\r\nHätt\' ich nur ein ganzes Meer, <br>\r\nMich hineinzutauchen;<br>\r\n\'s braucht nicht grad\' geprägt zu sein,<br> \r\nHab\'s auch so ganz gerne,<br>\r\nSei\'s des Mondes Silberschein,<br> \r\nSei\'s das Gold der Sterne,<br>\r\nSei\'s des Mondes Silberschein,<br> \r\nSei\'s das Gold der Sterne.<br>\r\n</p>\r\n<p>\r\nDoch viel schöner ist das Gold,<br> \r\nDas vom Lockenköpfchen<br>\r\nMeines Liebchens niederrollt<br>\r\nIn zwei blonde Zöpfchen.<br>\r\nDarum, du, mein liebes Kind,<br>\r\nLass uns herzend küssen,<br>\r\nBis die Locken silbern sind <br>\r\nUnd wir scheiden müssen,<br>\r\nBis die Locken silbern sind <br>\r\nUnd wir scheiden müssen.<br>\r\n</p>\r\n<p>\r\nSeht, wie blinkt der gold\'ne Wein<br> \r\nHier in meinem Becher;<br>\r\nHorcht, wie klingt so silberrein<br> \r\nFroher Sang der Zecher!<br>\r\nDass die Zeit einst golden war,<br>\r\nWill ich nicht bestreiten,<br>\r\nDenk\' ich doch im Silberhaar <br>\r\nGern vergang\'ner Zeiten,<br>\r\nDenk\' ich doch im Silberhaar<br>\r\nGern vergang\'ner Zeiten.<br>\r\n</p>\r\n'),
(9, 'Morgen kommt der Weihnachtsmann', 'Morgen kommt der Weihnachtsmann, Hoffman von Fallersleben (1835)', 'morgenkommtderweihnachtsmann', '<p>\r\nMorgen kommt der Weihnachtsmann,<br>\r\nKommt mit seinen Gaben.<br>\r\nTrommel, Pfeife und Gewehr,<br>\r\nFahn und Säbel und noch mehr,<br>\r\nJa ein ganzes Kriegesheer,<br>\r\nMöcht’ ich gerne haben.<br>\r\n</p>\r\n<p>\r\nBring’ uns, lieber Weihnachtsmann,<br>\r\nBring’ auch morgen, bringe<br>\r\nMusketier und Grenadier,<br>\r\nZottelbär und Panthertier,<br>\r\nRoß und Esel, Schaf und Stier,<br>\r\nLauter schöne Dinge. <br>                       \r\n</p>\r\n<p>\r\nDoch du weißt ja unsern Wunsch,<br>\r\nKennest unsere Herzen.<br>\r\nKinder, Vater und Mama,<br>\r\nAuch sogar der Großpapa,<br>\r\nAlle, alle sind wir da,<br>\r\nWarten dein mit Schmerzen.   <br>                     \r\n</p>\r\n'),
(10, 'Die Wacht am Rhein', 'Die Wacht am Rhein (1840)', 'diewachtamrhein', '<p>\r\nEs braust ein Ruf wie Donnerhall,<br>\r\nWie Schwertgeklirr und Wogenprall:<br>\r\nZum Rhein, zum Rhein, zum deutschen Rhein,<br>\r\nWer will des Stromes Hüter sein?<br>\r\n</p>\r\n<p>\r\nLieb\' Vaterland, magst ruhig sein,<br>\r\nFest steht und treu die Wacht am Rhein!<br>\r\n</p>\r\n<p>\r\nDurch hunderttausend zuckt es schnell,<br>\r\nUnd aller Augen blitzen hell;<br>\r\nDer deutsche Jüngling, fromm und stark,<br>\r\nBeschirmt die heil\'ge Landesmark.<br>                       \r\n</p>\r\n<p>\r\nLieb\' Vaterland, magst ruhig sein,<br>\r\nFest steht und treu die Wacht am Rhein!<br>\r\n</p>\r\n<p>\r\nEr blickt hinauf in Himmels Au\'n,<br>\r\nWo Heldengeister niederschau\'n,<br>\r\nUnd schwört mit stolzer Kampfeslust:<br>\r\nDu Rhein bleibst deutsch, wie meine Brust!<br>                     \r\n</p>\r\n<p>\r\nLieb\' Vaterland, magst ruhig sein,<br>\r\nFest steht und treu die Wacht am Rhein!<br>\r\n</p>\r\n<p>\r\nSo lang ein Tropfen Blut noch glüht,<br>\r\nNoch eine Faust den Degen zieht,<br>\r\nUnd noch ein Arm die Büchse spannt,<br>\r\nBetritt kein Feind hier deinen Strand!<br>                      \r\n</p>\r\n<p>\r\nLieb\' Vaterland, magst ruhig sein,<br>\r\nFest steht und treu die Wacht am Rhein!<br>\r\n</p>\r\n<p>\r\nUnd ob mein Herz im Tode bricht,<br>\r\nWirst du doch drum ein Welscher nicht,<br>\r\nReich, wie an Wasser deine Flut,<br>\r\nIst Deutschland ja an Heldenblut!<br>                     \r\n</p>\r\n<p>\r\nLieb\' Vaterland, magst ruhig sein,<br>\r\nFest steht und treu die Wacht am Rhein!<br>\r\n</p>\r\n<p>\r\nDer Schwur erschallt, die Woge rinnt,<br>\r\nDie Fahnen flattern hoch im Wind:<br>\r\nZum Rhein, zum Rhein, zum deutschen Rhein,<br>\r\nWir alle wollen Hüter sein!<br>                 \r\n</p>\r\n<p>\r\nLieb\' Vaterland, magst ruhig sein,<br>\r\nFest steht und treu die Wacht am Rhein!<br>\r\n</p>\r\n'),
(11, 'Am Weihnachtsbaum die Lichter brennen', 'Am Weihnachtsbaum die Lichter brennen, Hermann Kletke (1841)', 'amweihnachtsbaumdielichterbrennen', '<p>\r\nAm Weihnachtsbaum die Lichter brennen,<br>\r\nWie glänzt er festlich, lieb und mild,<br>\r\nAls spräch\' er: wollt in mir erkennen<br>\r\nGetreuer Hoffnung stilles Bild. <br>                       \r\n</p>\r\n<p>\r\nDie Kinder steh\'n mit hellen Blicken,<br>\r\nDas Auge lacht, es lacht das Herz;<br>\r\nO fröhlich\', seliges Entzücken!<br>\r\nDie Alten schauen himmelwärts.<br>\r\n</p>\r\n<p>\r\nZwei Engel sind hereingetreten,<br>\r\nKein Auge hat sie kommen seh\'n,<br>\r\nSie geh\'n zum Weihnachtsbaum und beten,<br>\r\nUnd wenden wieder sich und geh\'n: <br>                       \r\n</p>\r\n<p>\r\n„Gesegnet seid ihr alten Leute,<br>\r\nGesegnet sei du kleine Schar!<br>\r\nWir bringen Gottes Gaben heute<br>\r\nDem braunen, wie dem weißen Haar.<br>                       \r\n</p>\r\n<p>\r\nZu guten Menschen, die sich lieben,<br>\r\nSchickt uns der Herr als Boten aus,<br>\r\nUnd seid Ihr treu und fromm geblieben,<br>\r\nWir treten wieder in dies Haus!“  <br>                      \r\n</p>\r\n<p>\r\nKein Ohr hat ihren Spruch vernommen,<br>\r\nUnsichtbar jedes Menschen Blick,<br>\r\nSind sie gegangen, wie gekommen,<br>\r\nDoch Gottes Segen bleibt zurück!  <br>                      \r\n</p>\r\n'),
(12, 'Der Mai ist gekommen', 'Der Mai ist gekommen, Emanuel Geibel (1841)', 'dermaiistgekommen', '<p>\r\nDer Mai ist gekommen, die Bäume schlagen aus.<br>\r\nDa bleibe wer Lust hat, mit Sorgen zu Haus.<br>\r\nWie die Wolken dort wandern am himmlischen Zelt,<br>\r\nso steht auch mir der Sinn in die Weite, weite Welt.<br>\r\nHerr Vater, Frau Mutter, dass Gott euch behüt\'!<br>\r\nWer weiß, wo in der Ferne mein Glück mir noch blüht.<br>\r\nEs gibt so manche Straße, da nimmer ich marschiert;<br>\r\nes gibt so manchen Wein, den nimmer ich probiert.<br>\r\nFrisch auf drum, frisch auf im hellen Sonnenstrahl,<br>\r\nwohl über die Berge, wohl durch das tiefe Tal!<br>\r\nDie Quellen erklingen, die Bäume rauschen all-<br>\r\nmein Herz ist wie \'ne Lerche und stimmet ein mit Schall.<br>\r\nUnd abends im Städtchen, da kehr\' ich durstig ein:<br>\r\nHerr Wirt, mein Herr Wirt, eine Kanne blanken Wein!<br>\r\nErgreife die Fiedel, du lustiger Spielmann du,<br>\r\nvon meinem Schatz das Liedel, das sing\' ich dazu.<br>\r\nUnd find ich keine Herberg\', so lieg\' ich zur Nacht<br>\r\nwohl unter blauem Himmel, die Sterne halten Wacht.<br>\r\nIm Winde, die Linde, die rauscht mich ein gemach,<br>\r\nes küsset in der Früh\' das Morgenrot mich wach.<br>\r\nO Wandern, o wandern, du freie Burschenlust!<br>\r\nDa wehet Gottes Odem so frisch in der Brust;<br>\r\nda singet und jauchzet das Herz zum Himmelszelt:<br>\r\nWie bist du doch so schön, o du weite, weite Welt!<br>\r\n</p>\r\n'),
(13, 'Es ist ein Ros entsprungen', 'Es ist ein Ros entsprungen, Friedrich Layriz (1844)', 'esisteinrosentsprungen1844', '<p>\r\nEs ist ein Ros entsprungen<br>\r\naus einer Wurzel zart,<br>\r\nWie uns die Alten sungen,<br>\r\nvon Jesse kam die Art,<br>\r\nUnd hat ein Blümlein bracht,<br>\r\nmitten im kalten Winter,<br>\r\nwohl zu der halben Nacht.<br>\r\n</p>\r\n<p>\r\nDas Röslein, das ich meine,<br>\r\ndavon Jesaias sagt,<br>\r\nHat uns gebracht alleine<br>\r\nMarie, die reine Magd.<br>\r\nAus Gottes ewgem Rat<br>\r\nhat sie ein Kind geboren,<br>\r\nwohl zu der halben Nacht. <br>                      \r\n</p>\r\n<p>\r\nDas Röselein so kleine,<br>\r\ndas duftet uns so süß,<br>\r\nMit seinem hellen Scheine<br>\r\nvertreibts die Finsterniss.<br>\r\nWahr Mensch und wahrer Gott;<br>\r\nhilft uns aus allem Leide,<br>\r\nrettet von Sünd und Tod.   <br>                     \r\n</p>\r\n<p>\r\nLob, Ehr sei Gott dem Vater,<br>\r\ndem Sohn und heilgen Geist!<br>\r\nMaria, Gottesmutter,<br>\r\nsei hoch gebenedeit!<br>\r\nDer in der Krippen lag,<br>\r\nder wendet Gottes Zoren,<br>\r\nwandelt die Nacht in Tag.  <br>                      \r\n</p>\r\n<p>\r\nO Jesu, bis zum Scheiden<br>\r\naus diesem Jamerthal<br>\r\nLaß dein Hilf uns geleiten<br>\r\nhin in der Engel Saal,<br>\r\nIn deines Vaters Reich,<br>\r\nda wir dich ewig loben:<br>\r\no Gott, uns das verleih! <br>                      \r\n</p>\r\n'),
(14, 'Heckerlied', 'Heckerlied, Friedrich Hecker (1848)', 'heckerlied', '<p>\r\nWenn die Leute fragen, wenn die Leute fragen,<br>\r\nwenn die Leute fragen: \"Lebt der Hecker noch?\"<br>\r\nKönnt ihr ihnen sagen, könnt ihr ihnen sagen<br>\r\nKönnt ihr Ihnen sagen: \"Ja er lebet noch.\"<br>\r\n</p>\r\n<p>\r\nEr hängt an keinem Baum, und er hängt an keinem Strick<br>\r\nEr hängt an seinem Traum Von der freien Republik<br>\r\nEr hängt an keinem Baum, und er hängt an keinem Strick<br>\r\nEr hängt an seinem Traum Von der freien Republik <br>\r\n</p>\r\n<p>\r\nAn den Darm der Pfaffen, An den Darm der Pfaffen<br>\r\nAn den Darm der Pfaffen hängt den Edelmann<br>\r\nLaßt ihn dran erschlaffen, Laßt ihn dran erschlaffen<br>\r\nLaßt ihn dran erschlaffen, hängt ihn drauf und dran<br>\r\n</p>\r\n<p>\r\nJa 33 Jahre währt die Sauerei<br>\r\nWir sind keine Knechte, wir sind alle frei<br>\r\nJa 33 Jahre währt die Sauerei<br>\r\nWir sind keine Knechte, wir sind alle frei<br>\r\n</p>\r\n<p>\r\nSchmiert die Guillotine, Schmiert die Guillotine<br>\r\nSchmiert die Guillotine mit Tyrannenfett!<br>\r\nReisst die Konkubine, Reisst die Konkubine<br>\r\nReisst die Konkubine aus dem Pfaffenbett !<br>\r\n</p>\r\n<p>\r\nJa 33 Jahre währt die Sauerei<br>\r\nWir sind keine Knechte, wir sind alle frei<br>\r\nJa 33 Jahre währt die Sauerei<br>\r\nWir sind keine Knechte, wir sind alle frei<br>\r\n</p>\r\n<p>\r\nFürstenblut muss fließen, Fürstenblut muss fließen<br>\r\nFürstenblut muss fließen fließen stiefeldick !<br>\r\nUnd daraus ersprießen, Und daraus ersprießen<br>\r\nUnd daraus ersprieß\'n die freie Republik<br>\r\n</p>\r\n<p>\r\nJa hunderttausend Jahre währt die Knechtschaft schon<br>\r\nNieder mit den Hunden von der Reaktion<br>\r\nJa hunderttausend Jahre währt die Knechtschaft schon<br>\r\nNieder mit den Hunden von der Reaktion<br>\r\n</p>\r\n'),
(15, 'Als die Römer frech geworden', 'Als die Römer frech geworden, Joseph von Scheffel (1849)', 'alsdieroemerfrechgeworden', '<p>\r\nAls die Römer frech geworden,<br>\r\nSim serim sim sim sim sim,<br>\r\nZogen sie nach Deutschlands Norden,<br>\r\nSim serim sim sim sim sim,<br>\r\nVorne mit Trompetenschall,<br>\r\nTe rä tä tä tä te rä,<br>\r\nRitt der Generalfeldmarschall,<br>\r\nTe rä tä tä tä te rä,<br>\r\nHerr Quintilius Varus,<br>\r\n</p>\r\n<p>\r\nWau, wau, wau, wau, wau,<br>\r\nHerr Quintilius Varus,<br>\r\nSchnäde räng täng<br>\r\nSchnäde räng täng, de räng täng täng <br>                       \r\n</p>\r\n<p>\r\nIn dem Teutoburger Walde,<br>\r\nHuh! Wie pfiff der Wind so kalte,<br>\r\nRaben flogen durch die Luft,<br>\r\nUnd es war ein Moderduft,<br>\r\nWie von Blut und Leichen.  <br>                      \r\n</p>\r\n<p>\r\nPlötzlich aus des Waldes Duster<br>\r\nBrachen krampfhaft die Cherusker,<br>\r\nMit Gott für König und Vaterland<br>\r\nStürtzten sie sich wutentbrand<br>\r\nAuf die Legionen.          <br>              \r\n</p>\r\n<p>\r\nWeh! das ward ein großes Morden,<br>\r\nSie schlugen die Kohorten;<br>\r\nNur die römsche Reiterei<br>\r\nRettete sich in das Frei\',<br>\r\nDenn sie war zu Pferde.  <br>                     \r\n</p>\r\n<p>\r\nO! Quintili, armer Feldherr!<br>\r\nDachtest Du, daß so die Welt wär?<br>\r\nEr geriet in einen Sumpf,<br>\r\nVerlor zwei Stiefel und einen Strumpf<br>\r\nUnd blieb elend stecken.     <br>                  \r\n</p>\r\n<p>\r\nDa sprach er voll Ärgernussen<br>\r\nZum Centurio Titiussen:<br>\r\n\"Kamerad, zeuch Dein Schwert hervor<br>\r\nUnd von hinten mich durchbohr,<br>\r\nWeil doch alles futsch ist!\" <br>                       \r\n</p>\r\n<p>\r\nIn dem armen römischen Heere<br>\r\nDiente auch als Volontaire<br>\r\nScävola, ein Rechtskandidat,<br>\r\nDen man schnöd\' gefangen hat,<br>\r\nWie die andern alle.<br>\r\n8. Diesem ist es schlimm ergangen,<br>\r\nEh\' daß man ihn aufgehangen,<br>\r\nStach man ihm durch Zung\' und Herz,<br>\r\nNagelte ihn hinterwärts<br>\r\nAuf sein corpus juris. <br>                       \r\n</p>\r\n<p>\r\nAls das Morden war zu Ende,<br>\r\nRieb Fürst Hermann sich die Hände,<br>\r\nUnd um sich noch mehr zu freu\'n,<br>\r\nLud er die Cherusker ein<br>\r\nZu \'nem großen Frühstück.   <br>                    \r\n</p>\r\n<p>\r\nWild gab\'s und westfäl\'schen Schinken,<br>\r\nBier, soviel man wollte trinken.<br>\r\nAuch im Zechen blieb er Held,<br>\r\nDoch auch seine Frau Thusneld,<br>\r\nTrank walkürenmäßig.          <br>              \r\n</p>\r\n<p>\r\nNur in Rom war man nicht heiter,<br>\r\nSondern kaufte Trauerkleider;<br>\r\nGrade als beim Mittagsmahl<br>\r\nAugustus saß im Kaisersaal,<br>\r\nKam die Trauerbotschaft.   <br>                     \r\n</p>\r\n<p>\r\nErst blieb ihm vor jähem Schrecken<br>\r\nEin Stück Pfau im Halse stecken,<br>\r\nDann geriet er außer sich\"<br>\r\n\"Varus, Varus, schäme Dich,<br>\r\nRedde legiones!\"     <br>                   \r\n</p>\r\n<p>\r\nSein deutscher Sklave, Schmidt geheißen,<br>\r\nDacht: Ihn soll das Mäusle beißen,<br>\r\nWenn er je sie wieder kriegt,<br>\r\nDenn wer einmal tot da liegt,<br>\r\nWird nicht mehr lebendig.  <br>                      \r\n</p>\r\n<p>\r\nNeunzehnhundert Jahr\' verflossen,<br>\r\nSeit hier Römer Blut vergossen,<br>\r\nDas uns Knechtschaft zugedacht.<br>\r\nHermanns Denkmal hält nun Wacht<br>\r\nDeutscher Kraft und Stärke.   <br>                     \r\n</p>\r\n<p>\r\nUnd zu Ehren der Geschichten<br>\r\nTat ein Denkmal man errichten,<br>\r\nDeutschlands Kraft und Einigkeit<br>\r\nKündet es jetzt weit und breit:<br>\r\n\"Mögen sie nur kommen!\"  <br>                      \r\n</p>\r\n<p>\r\nUm des Denkmals Glanz zu mehren<br>\r\nLaßt uns seinen Schöpfer ehren!<br>\r\nDeinen Namen trag die Bank<br>\r\nBandel, die wir Dir zum Dank<br>\r\nUnd zur Ehr\' erbauten.       <br>                 \r\n</p>\r\n'),
(16, 'Jetzt kommen die lustigen Tage', 'Jetzt kommen die lustigen Tage (~1850)', 'jetztkommendielustigentage', '<p>\r\nJetzt kommen die lustigen Tage<br>\r\nSchätzel Ade!<br>\r\nUnd dass ich es dir nur sage<br>\r\nEs tut mir gar nicht weh;<br>\r\nUnd im Sommer da blüht<br>\r\nDer rote, rote Mohn<br>\r\nUnd ein lustiges Blut<br>\r\nKommt überall davon.<br>\r\nSchätzel Ade, Ade<br>\r\nSchätzel Ade.<br>\r\n</p>\r\n<p>\r\nUnd morgen da müssen wir wandern<br>\r\nSchätzel ade!<br>\r\nUnd küsst du gleich einen andern<br>\r\nWenn ich es nur nicht seh\'<br>\r\nUnd seh ich\'s im Traum<br>\r\nSo bild ich mir denn ein<br>\r\nDas ist gar nicht so<br>\r\nSo kanns ja gar nicht sein.<br>\r\nSchätzel Ade, Ade.<br>\r\nSchätzel Ade.<br>\r\n</p>\r\n<p>\r\nUnd kehr ich dann einstmals wieder<br>\r\nSchätzel Ade!<br>\r\nSo sing ich die alten Lieder<br>\r\nVorbei ist all mein Weh,<br>\r\nUnd bist du mir treu<br>\r\nWie einstmals im Mai<br>\r\nSo bleib ich bei dir<br>\r\nAuf ewige Treu<br>\r\nSchätzel Ade, Ade,<br>           \r\n</p>\r\n<p>\r\nSchätzel Ade.\r\n</p>\r\n'),
(17, 'Im schwarzen Walfisch zu Askalon', 'Im schwarzen Walfisch zu Askalon, Joseph von Scheffel (1854)', 'imschwarzenwalfischzuaskalon', '<p>\r\nIm Schwarzen Walfisch zu Askalon da<br>\r\ntrank ein Mann drei Tag\',<br>\r\nbis daß er steif wie\'n Besenstiel<br>\r\nam Marmortischelag.<br>\r\n</p>\r\n<p>\r\nIm Schwarzen Walfisch zu Askalon,<br>\r\nda sprach der Wirt: \"Halt an!<br>\r\nDer trinkt von meinem Dattelsaft mehr,<br>\r\nals er zahlen kann!\"<br>\r\n</p>\r\n<p>\r\nIm Schwarzen Walfisch zu Askalon,<br>\r\nda bracht\' der Kellner Schar,<br>\r\nIn Keilschrift auf sechs Ziegelstein<br>\r\ndem Gast die Rechnung dar.<br>\r\n</p>\r\n<p>\r\nIm Schwarzen Walfisch zu Askalon,<br>\r\nda sprach der Gast: \"O weh!<br>\r\nMein braves Geld ging alles drauf<br>\r\nim Lamm zu Ninive!\" <br>  \r\n</p>\r\n<p>\r\nIm Schwarzen Walfisch zu Askalon,<br>\r\nda schlug die Uhr halb vier,<br>\r\nDa warf der Hausknecht aus Nubierland<br>\r\nden Fremden vor die Tür!  <br>\r\n</p>\r\n<p>\r\nIm schwarzen Walfisch zu Askalon<br>\r\nwird kein Prophet geehrt,<br>\r\nund wer vergnügt dort leben will,<br>\r\nzahlt bar, was er verzehrt.<br>\r\n</p>\r\n'),
(18, 'O du stille Zeit', 'O du stille Zeit, Joseph von Eichendorff (1857)', 'odustillezeit', '<p>\r\nO du stille Zeit,<br>\r\nKommst, eh wir´s gedacht<br>\r\nüber die Berge weit,<br>\r\nüber die Berge weit<br>\r\nGute Nacht!<br>\r\n</p>\r\n<p>\r\nIn der Einsamkeit<br>\r\nrauscht es nun sacht,<br>\r\nÜber die Berge weit,<br>\r\nüber die Berge weit,<br>\r\nGute Nacht!<br>\r\n</p>\r\n'),
(19, 'O Deutschland hoch in Ehren', 'O Deutschland hoch in Ehren, Ludwig Bauer (1859)', 'odeutschlandhochinehren', '<p>\r\nO Deutschland hoch in Ehren,<br>\r\nDu heil\'ges Land der Treu,<br>\r\nStets leuchte deines Ruhmes Glanz<br>\r\nIn Ost und West aufs neu!<br>\r\nDu stehst wie deine Berge fest<br>\r\nGen Feindes Macht und Trug,<br>\r\nUnd wie des Adlers Flug vom Nest<br>\r\ngeht deines Geistes Flug.<br>\r\n</p>\r\n<p>\r\nHaltet aus! Haltet aus!<br>\r\nLasset hoch das Banner wehn!<br>\r\nZeiget ihm, zeigt dem Feind,<br>\r\nDaß wir treu zusammen stehn,<br>\r\nDaß sich unsre alte Kraft erprobt,<br>\r\nWenn der Schlachtruf uns entgegen tobt!<br>\r\nHaltet aus im Sturmgebraus! <br>                       \r\n</p>\r\n<p>\r\nGedenket eurer Väter!<br>\r\nGedenkt der großen Zeit<br>\r\nDa Deutschlands gutes Ritterschwert<br>\r\nGesiegt in jedem Streit!<br>\r\nDas sind die alten Schwerter noch,<br>\r\nDas ist das deutsche Herz:<br>\r\nDie schlagt ihr nimmermehr ins Joch,<br>\r\nSie dauern fest wie Erz!   <br>                     \r\n</p>\r\n<p>\r\nHaltet aus! Haltet aus!<br>\r\nLasset hoch das Banner wehn!<br>\r\nZeiget stolz, zeigt der Welt,<br>\r\nDaß wir treu zusammenstehn,<br>\r\nDaß sich alte deutsche Kraft erprobt,<br>\r\nOb uns Friede strahlt, ob Krieg umtobt!<br>\r\nHaltet aus im Sturmgebraus!   <br>                     \r\n</p>\r\n<p>\r\nZum Herrn erhebt die Hände:<br>\r\nEr schirm\' es immerdar,<br>\r\nDas schöne Land, vor jedem Feind.<br>\r\nHoch steige, deutscher Aar!<br>\r\nDem teuren Lande Schirm und Schutz<br>\r\nSei, deutscher Arm, bereit!<br>\r\nWir bieten jedem Feinde Trutz<br>\r\nUnd scheuen keinen Streit. <br>                       \r\n</p>\r\n<p>\r\nHaltet aus! Haltet aus!<br>\r\nLasset hoch das Banner wehn!<br>\r\nLasset uns treu und kühn<br>\r\nMit den ersten Völkern gehn!<br>\r\nDaß sich deutscher Geist und Kraft erprobt,<br>\r\nWenn das Ungewitter uns umtobt!<br>\r\nHaltet aus im Sturmgebraus!  <br>                      \r\n</p>\r\n'),
(20, 'Süßer die Glocken nie klingen', 'Süßer die Glocken nie klingen, Friedrich Kritzinger (1860)', 'suesserdieglockennieklingen', '<p>\r\nSüßer die Glocken nie klingen<br>\r\nals zu der Weihnachtszeit,<br>\r\n’s ist, als ob Engelein singen<br>\r\nwieder von Frieden und Freud’.<br>\r\n</p>\r\n<p>\r\nWie sie gesungen in seliger Nacht,<br>\r\nGlocken mit heiligem Klang,<br>\r\nklinget die Erde entlang!<br>                       \r\n</p>\r\n<p>\r\nO, wenn die Glocken erklingen,<br>\r\nschnell sie das Christkindlein hört:<br>\r\nTut sich vom Himmel dann schwingen<br>\r\neilig hernieder zur Erd’.<br>\r\n</p>\r\n<p>\r\nSegnet den Vater, die Mutter, das Kind, <br>\r\nGlocken mit heiligem Klang,<br>\r\nklinget die Erde entlang!<br>                       \r\n</p>\r\n<p>\r\nKlinget mit lieblichem Schalle<br>\r\nüber die Meere noch weit,<br>\r\ndass sich erfreuen doch alle<br>\r\nseliger Weihnachtszeit. <br>                       \r\n</p>\r\n<p>\r\nAlle aufjauchzen mit herrlichem Sang!<br>\r\nGlocken mit heiligem Klang,<br>\r\nklinget die Erde entlang!<br>                        \r\n</p>\r\n'),
(21, 'Morgen will mein Schatz verreisen', 'Morgen will mein Schatz verreisen (1866)', 'morgenwillmeinschatzverreisen', '<p>\r\nMorgen will mein Schatz abreisen,<br>\r\nAbschied nehmen mit Gewalt;<br>\r\ndraußen singen schon die Vögel,<br>\r\nsingen schon die Vögel im dunkelgrünen Wald<br>\r\nUnd es fällt mir so schwer,<br>\r\naus der Heimat zu gehn,<br>\r\nwenn die Hoffnung nicht wär<br>\r\nauf ein Wiedersehn.<br>\r\nLebewohl, lebewohl lebewohl,<br>\r\nlebewohl, lebewohl, auf Wiedersehn!<br>\r\n</p>\r\n<p>\r\nSaßen da zwei Turteltauben,<br>\r\nbeide auf \'nem grünen Ast:<br>\r\nWo sich zwei Verliebte scheiden,<br>\r\nda verwelket Laub und Gras.<br>\r\n</p>\r\n<p>\r\nLaub und Gras, das mag verwelken,<br>\r\naber unsre Liebe nicht.<br>\r\nDu kommst mir aus meinen Augen,<br>\r\naber aus dem Herzen nicht.<br>\r\n</p>\r\n<p>\r\nEine Schwalbe macht kein\' Sommer,<br>\r\nob sie gleich die erste ist;<br>\r\nund mein Liebchen macht mir Kummer,<br>\r\nob sie gleich die Schönste ist.<br>\r\n</p>\r\n<p>\r\nSpielet auf, ihr Musikanten,<br>\r\nspielet auf ein Abschiedslied,<br>\r\nmeinem Liebchen zum Gefallen,<br>\r\nmags verdrießen, wen es will.<br>\r\n</p>\r\n');

