INSERT INTO `song_1918` (`song_number_1918`, `title`, `display`, `link`, `content`) VALUES
(1, 'Auf Gesellen froh und munter', 'Auf Gesellen froh und munter (1918)', 'aufgesellenfrohundmunter', '<p>\r\nAuf Gesellen froh und munter<br>\r\nschenkt die Gläser voller Wein<br>\r\ndenn unser Handwerk geht niemals unter<br>\r\nlustige Zimmerleute wollen wir sein<br>\r\n</p>\r\n<p>\r\nMeister gib uns die Papiere<br>\r\nMeister gib uns unser Geld<br>\r\ndie schönen Mädchen , die sind uns lieber<br>\r\nals die Schafferei auf dieser Welt<br>\r\n</p>\r\n<p>\r\nDenn wir brauchen´s keinen Kaiser<br>\r\nund wir brauchen´s keinen Gott<br>\r\nDenn der Krieg war nur für Reiche<br>\r\nund der arme Teufel ging kaputt<br>\r\n</p>\r\n'),
(2, 'Wenn die Arbeitszeit zu Ende', 'Wenn die Arbeitszeit zu Ende, Jürgen Brand (1919)', 'wenndiearbeitszeitzuende', '<p>\r\nWenn die Arbeitszeit zu Ende<br>\r\nrüsten wir nach Burschenart<br>\r\nSamstag alle fleißigen Hände<br>\r\nzu der frohen Wanderfahrt.<br>\r\nSingend ziehn wir aus dem Städtchen<br>\r\nfrei das Herz und leicht der Sinn<br>\r\nlinks die Burschen, rechts die Mädchen<br>\r\nund ich selber mittendrin.<br>\r\n</p>\r\n<p>\r\nHei, das ist ein fröhlich Wandern!<br>\r\nWiesen, Felder ziehn vorbei.<br>\r\nEiner sagt es froh dem andern:<br>\r\nHeute, Brüder, sind wir frei!<br>\r\nWeit zurück liegt schon das Städtchen<br>\r\nund wir wandern leicht dahin,<br>\r\nlinks die Burschen, rechts die Mädchen<br>\r\nund ich selber mittendrin.<br>\r\n</p>\r\n<p>\r\nSingen, spielen im Vereine,<br>\r\nRast in kühler Waldesruh.<br>\r\nUnd beim hellen Mondenscheine<br>\r\nwandern wir der Heimat zu.<br>\r\nSingend ziehn wir ein ins Städtchen<br>\r\nfrei das Herz und leicht der Sinn<br>\r\nlinks die Burschen, rechts die Mädchen<br>\r\nund ich selber mittendrin.<br>\r\n</p>\r\n'),
(3, 'Trum, trum, terum tum tum', 'Trum, trum, terum tum tum, Walter Gättke (1919)', 'trumtrumterumtumtum', '<p>\r\nTrum, trum, terum tum tum<br>\r\nDie Landsknecht zieh\'n im Land herum<br>\r\nTrum, trum, terum tum tum<br>\r\nMit Trommeldröhnen und Gebrumm<br>\r\nEs schrillen die Flöten<br>\r\nDas Kriegsvolk, es singt<br>\r\nEs flattern die Fahnen<br>\r\nEs jauchzt und es klingt<br>                        \r\n</p>\r\n<p>\r\nHei, hei, heißa! juchei!<br>\r\nDie Wallensteiner zieh\'n vorbei<br>\r\nHei, hei, heißa juchei<br>\r\nTrum, trum, terum tum tum<br>\r\nTrum, trum, terum tum tum<br>                        \r\n</p>\r\n<p>\r\nTrum, trum, terum tum tum<br>\r\nUnd wieder geht die Trommel um<br>\r\nTrum, trum, terum tum tum<br>\r\nSie wird nicht müde und nicht stumm<br>\r\nSie dreuet dem Schweden im blutigen Krieg<br>\r\nWir hör\'n sie beim Sterben <br>                       \r\n</p>\r\n<p>\r\nWir hör\'n sie beim Sieg<br>\r\n</p>\r\n'),
(4, 'Auf Spieler die ihr hier vereint', 'Auf Spieler die ihr hier vereint (~1920)', 'aufspielerdieihrvereint', '<p>\r\nAuf Spieler die ihr hier vereint<br>\r\nstolz wir das Wort uns geben<br>\r\nSolang des Lebens Sonne scheint<br>\r\ndem Fußballsport zu leben<br>\r\n</p>\r\n<p>\r\nKollegen nennt darum ihr Euch<br>\r\nkollegial das Leben<br>\r\npflegt Treue, Freundschaft, Einigkeit<br>\r\num so den Sport zu heben<br>\r\n</p>\r\n<p>\r\nTreu zugetan dem Fußballsport<br>\r\nwenn´s not tut brav gestritten<br>\r\nsei das Losungswort<br>\r\neinfach und schlicht in Sitten<br>\r\n</p>\r\n<p>\r\nDrum Brüder tretet in die Bahn<br>\r\nmit festem Mut und Willen<br>\r\nwenn Richters Pfeife kündet an<br>\r\ndes heißen Kampf´s Beginnen<br>\r\n</p>\r\n<p>\r\nEin Hoch! Ein Heil<br>\r\nim Freundesfußballkreise<br>\r\nes grüßt uns allzuweil<br>\r\nnach allbekannter Weise<br>\r\n</p>\r\n<p>\r\nWer Fußballsport sein eigen nennt<br>\r\nhält ihn zu jeder Stunde<br>\r\nund sei der Worte eingedenk<br>\r\ngegeben in Festesrunde<br>\r\n</p>\r\n'),
(5, 'Auf weiter grüner Heide', 'Auf weiter grüner Heide (~1920)', 'aufweitergruenerheide', '<p>\r\nAuf weiter grüner Heide<br>\r\nda such ich meine Freude<br>\r\nbeim schönen Fußballspiel<br>\r\nIn frischer Luft zu leben<br>\r\nmuß Kraft und Geist uns geben<br>\r\ndas ist ein edles Ziel<br>\r\n</p>\r\n<p>\r\nWo Alltagssorgen drücken<br>\r\nda müssen sie entrücken<br>\r\nvor schönem Fußballspiel<br>\r\nDer Griesgram wird geschlagen<br>\r\ner kann es nicht ertragen<br>\r\nes wird ihm allzuviel<br>\r\n</p>\r\n<p>\r\nStets Freundschaft zu erneuen<br>\r\nversammelt die Getreuen<br>\r\ndas schöne Fußballspiel<br>\r\nMag Mond und Stern auch sinken<br>\r\nund Liebe eifrig winken<br>\r\nwir stehen treu zum Spiel<br>\r\n</p>\r\n<p>\r\nDrum will ich´s immer treiben<br>\r\nein tapfer Spieler bleiben<br>\r\nbeim schönen Fußballspiel<br>\r\nAuf Brüder nehmt die Becher<br>\r\nund ruft als wackre Zecher<br>\r\nEin Hoch dem Fußballspiel<br>\r\n</p>\r\n'),
(6, 'Das ist das Lied vom täglichen Brot', 'Das ist das Lied vom täglichen Brot, Jörg Mager (1920)', 'dasistdasliedvomtaeglichenbrot', '<p>\r\nDas ist das Lied vom täglichen Brot<br>\r\ndie es erschaffen, leiden Not.<br>\r\nDie Kleider wirken – gehen bloß.<br>\r\nDie Häuser bauen – wohnungslos.<br>\r\nDas ist das Lied vom Brot.<br>\r\n</p>\r\n<p>\r\nDas ist das Lied vom alten Geschlecht<br>\r\nDem Herrn das Land, die Fron dem Knecht<br>\r\nDie Kohlen graben – ohne Herd,<br>\r\nDie Werte schaffen – ohne Wert.<br>\r\nDas ist das Lied vom Geschlecht.<br>\r\n</p>\r\n<p>\r\nDas ist das Lied der höllischen Pein.<br>\r\nDem Reichen Brot, dem Armen Stein.<br>\r\nDem Armen Nacht und bittres Muß<br>\r\nDem Reichen Glanz und Überfluß.<br>\r\nDas ist das Lied der Pein<br>\r\n</p>\r\n<p>\r\nDas ist das Lied, wenn der Aufruhr gellt<br>\r\nWenn alte Schmach an uns zerschellt<br>\r\nDas ist das Lied, das nicht verzeiht<br>\r\nIhr Knechte, seid zur Tat bereit<br>\r\nWenn der Aufruhr gellt<br>\r\n</p>\r\n'),
(7, 'Die Grenzwacht hielt im Osten', 'Die Grenzwacht hielt im Osten, Manteuffel-Katzdangen (1920)', 'diegrenzwachthieltimosten', '<p>\r\nDie Grenzwacht hielt im Osten, dem Feinde lange stand,<br>\r\nHeut kehrt ihr letzter Posten, zurück ins Vaterland.<br>\r\nErschöpft und aufgerieben, in treuer Ritterschaft,<br>\r\nDie Besten sind geblieben, uns ander\'n brach die Kraft.<br>\r\n</p>\r\n<p>\r\nDoch bringen wir die Fahne, die wehend vor uns stritt,<br>\r\nVon Rigas blut\'gem Plane, in allen Ehren mit.<br>\r\nDie sturmbewährt sich nimmer, vor einem Feind geneigt,<br>\r\nUnd heute noch und immer, den Weg nach Osten zeigt.<br>                                             \r\n</p>\r\n<p>\r\nEs rauscht dorthin zu mahnen, zu ihr der Väter Geist,<br>\r\nTrotz aller Not ein Ahnen, das deutsche Zukunft heißt.<br>\r\nSind wir auch fremd geworden, euch Brüdern aus dem Reich,<br>\r\nAus West und Süd und Norden, das Banner blieb sich gleich.<br>                      \r\n</p>\r\n<p>\r\nOb wir auch hier verderben, das kümmere euch nicht,<br>\r\nDie Fahne zu vererben, ist unsere letzte Pflicht.<br>\r\nIch darf nicht länger zagen, bald zwingt sie euren Sinn,<br>\r\nNach Ostland sie zu tragen, sie will sie muss dorthin.<br>\r\nNach Ostland sie zu tragen, sie will sie muss dorthin.<br>                      \r\n</p>\r\n'),
(8, 'Es klappert der Huf am Stege', 'Es klappert der Huf am Stege, Hans Riedel (1920)', 'esklappertderhufamstege', '<p>\r\nEs klappert der Huf am Stege,<br>\r\nWir ziehn mit dem Fähnlein ins Feld;<br>\r\nBlut´ger Kampf allerwege,<br>\r\nDazu sind auch wir bestellt.<br>\r\nWir reiten und reiten und singen,<br>\r\nIm Herzen die bitterste Not.<br>\r\nDie Sehnsucht will uns bezwingen<br>\r\nDoch wir reiten die Sehnsucht tot.<br>\r\n</p>\r\n<p>\r\nDörfer und Städte flogen<br>\r\nVorüber an unserem Blick.<br>\r\nWir sind immer weiter gezogen,<br>\r\nFür uns gibt es kein Zurück.<br>\r\nWir reiten durch Täler und Hügel,<br>\r\nWo der Sommer in Blüte steht;<br>\r\nEs knirschen Zaumzeug und Zügel,<br>\r\nDer Wimpel hoch über uns weht.<br>\r\n</p>\r\n<p>\r\nLeis sinkt der Abend nieder,<br>\r\nUns wird das Herz so schwer<br>\r\nLeiser werden die Lieder<br>\r\nWir sehn keine Heimat mehr<br>\r\nWir reiten und reiten und reiten<br>\r\nUnd hören von fern schon die Schlacht<br>\r\nHerr, lass uns stark sein im Streiten<br>\r\nDann sei unser Leben vollbracht.<br>\r\n</p>\r\n'),
(9, 'Der Winter der is mir net z´wieder', 'Der Winter der is mir net z´wieder, Otto Sirl (~1921)', 'derwinterderismirnetzwieder', '<p>\r\nDer Winter der is mir net z´wieder<br>\r\nda gfreu i mi deng erscht net schlecht<br>\r\nund fallen die Flocken hernieder<br>\r\nDös schneien, dös is mir grad recht<br>\r\nund is dann das Land weiß umsponnen<br>\r\nfür mi gibt´s besondere Wonnen<br>\r\nZwoa Brettl, a gführiger Schnee, juchhe<br>\r\ndös is halt mei höchste Idee<br>\r\n</p>\r\n<p>\r\nNa ziag i gschwind an mei fesch Gwandl<br>\r\nSetz d´Mützen recht keck übers Ohr<br>\r\nBind d´Hosen fest zua mit an Bandl<br>\r\nUnd hol meine Schwartling hervor.<br>\r\nDen Schnerfer, den schweren, geschnüret<br>\r\nSki heil! und jetzt wird abmarschiert<br>\r\nZwoa Brettl a gführiger Schnee: juchhe<br>\r\ndös is halt mei höchste Idee<br>\r\n</p>\r\n<p>\r\nIn d´Berg hinein tuats mi halt ziagen<br>\r\nda hatsch i schön langsam auf d´Höh<br>\r\nDa gspür i koa Plag, koane Mühen<br>\r\nkoan Hunger, koa Durst tut ma weh<br>\r\nbevor i das Ziel mir errungen<br>\r\nund Schnee und Eis trotzig bezwungen<br>\r\nZwoa Brettl a gführiger Schnee: juchhe<br>\r\ndös is halt mei höchste Idee<br>\r\n</p>\r\n<p>\r\nUnd steh i na drobn auf da Spitz´n<br>\r\nund schaug i rund um in der Welt<br>\r\nna ziag i voll Andacht mei Mütz´n<br>\r\na Bazi wem so was net gefällt<br>\r\nvoll Freud tuat mir´s Herz überquellen<br>\r\nda tua ri an Juhschroa, an hellen<br>\r\nZwoa Brettl a gführiger Schnee: juchhe<br>\r\ndös is halt mei höchste Idee<br>\r\n</p>\r\n<p>\r\nUnd wenn na die Brettl so rennen<br>\r\nauf glitzernder Bahn von der Höh<br>\r\nMei Liaba, dös muaß ma schon könna<br>\r\nsonst haut´s di ganz damisch in Schnee<br>\r\nDoch kannst amal fahren recht prächti<br>\r\ndann packt di die Leidenschaft mächti:<br>\r\nZwoa Brettl a gführiger Schnee: juchhe<br>\r\ndös is halt mei höchste Idee<br>\r\n</p>\r\n<p>\r\nDoch dös steht scho fest ohne Zweifi<br>\r\npassieren kann da allerlei<br>\r\nfahrst nei in an Grabn wia da Teifi<br>\r\nund knacks is a Schwartling entzwei<br>\r\nda stehst na und schaugst allweil dümmer<br>\r\nund hast in der Hand deine Trümmer:<br>\r\nDrei Brettl, a gführiger Schnee, o weh<br>\r\nDa hast jetzt dei höchste Idee<br>\r\n</p>\r\n<p>\r\nDoch sitz ma schön warm in der Hütten<br>\r\nes prasselt das Feuer im Herd<br>\r\nden dampfenden Tee in der Mitten<br>\r\nund Speck und Brot, wia´s a si g´hört!<br>\r\nDann laßt die Gitarre erklingen<br>\r\ndem Winter ein Loblied zu singen:<br>\r\nZwoa Brettl a gführiger Schnee: juchhe<br>\r\ndös is halt mei höchste Idee<br>\r\n</p>\r\n<p>\r\nUnd kommt dann der Mai in die Lande<br>\r\nund es regnet und donnert und blitzt<br>\r\ner sprenget die eisigen Bande<br>\r\nund der Skiläufer keuchet und schwitzt<br>\r\nDann trägt er wohl 2000 Meter<br>\r\nin d´Berg hinauf seine Bretter<br>\r\nZwoa Brettl und nirgends a Schnee, o Weh<br>\r\nis dös a verrückte Idee<br>\r\n</p>\r\n<p>\r\nWas wär ohne Brettl mei Leben<br>\r\nja, Angst wär´s mir nacha und bang<br>\r\nMei Gut und Blut tat i drum geben<br>\r\nmei Alls bleib´´ n mei ganz Leben lang<br>\r\nUnd ruft mich der Tod einst zum Gehen<br>\r\nso soll auf mei mGrabstein no stehen<br>\r\nZwoa Brettl a gführiger Schnee: juchhe<br>\r\ndös war halt sei höchste Idee<br>\r\n</p>\r\n'),
(10, 'Ihr könnt das Wort verbieten', 'Ihr könnt das Wort verbieten, Henry Mackay (1921)', 'ihrkoenntdaswortverbieten', '<p>\r\nIhr könnt das Wort verbieten<br>\r\nihr tötet nicht den Geist<br>\r\nDer über Eurer Lüge<br>\r\nein kühner Adler, kreist<br>\r\nIhr könnt das Wort verbieten<br>\r\ndoch rollen wird sein Schall<br>\r\nHin über Eure Häupter<br>\r\nin dumpfem Widerhall<br>\r\n</p>\r\n<p>\r\nSo lange wird es rufen<br>\r\nzur Tat die schlaffe Zeit,<br>\r\nWie nach der trägen Mutter<br>\r\ndas Kind verlangend schreit,<br>\r\nBis auf den höchsten Höhen<br>\r\nbis in dem tiefsten Schacht<br>\r\nDer Mensch zum letzten Kampfe<br>\r\nsich aufrafft und erwacht.<br>\r\n</p>\r\n<p>\r\nHei, wie die Steine fallen<br>\r\nvon Eurer festen Burg!<br>\r\nDurch die gestürzten Mauern<br>\r\nglänzt schon das Frühlicht durch!<br>\r\nUnd wenn auch mancher sterbend<br>\r\nan Eurer Lüge sinkt,<br>\r\nSich auf den leeren Posten<br>\r\nein neuer Kämpfer schwingt!<br>\r\n</p>\r\n<p>\r\nIhr mögt sein Wort verbieten!<br>\r\nIch sehe seinen Geist,<br>\r\nWie er, ein kühner Adler<br>\r\nob Eurer Schande kreist! –<br>\r\nDann steigt auf toten Trümmern<br>\r\ndie neue Zeit empor,<br>\r\nUnd allen leiht sie freundlich<br>\r\nihr immer offnes Ohr!<br>\r\n</p>\r\n<p>\r\nDann werden die Tage kommen<br>\r\nwo nicht mehr fort und fort<br>\r\nDas Wort der bangen Sehnsucht<br>\r\nauf durstigen Lippen dorrt;<br>\r\nWo keiner Frevel nennen<br>\r\ndie kühne Wahrheit darf,<br>\r\nWenn sie den Fluch der Lüge<br>\r\nbeleuchtet grell und scharf!<br>\r\n</p>\r\n<p>\r\nDann sind wir endlich Sieger!<br>\r\nUnd Euch, Euch bleibt die Schmach.<br>\r\nDie auf dem Weg der Freiheit<br>\r\nein früher Schatten lag! –<br>\r\nNoch könnt Ihr es verbieten<br>\r\ndas Wort, doch schon sein Geist<br>\r\nHoch über Eurer Lüge<br>\r\nein freier Adler, kreist<br>\r\n</p>\r\n'),
(11, 'Wir ziehen über die Straßen', 'Wir ziehen über die Straßen, Knott (1921)', 'wirziehenueberdiestrassen', '<p>\r\nWir ziehen über die Straßen<br>\r\nin schweren Schritt und Tritt,<br>\r\nund über uns die Fahne,<br>\r\nsie knallt und flattert mit.<br>\r\n</p>\r\n<p>\r\nDon di ri, don di ri<br>\r\nDi ri di ri don, di ri don.<br>\r\nDon di ri, don di ri,<br>\r\nDi ri di ri don.<br>\r\n</p>\r\n<p>\r\nVoran der Trommelbube,<br>\r\ner schlägt die Trommel gut,<br>\r\ner weiß noch nichts von Liebe,<br>\r\nweiß nicht, wie Scheiden tut.<br>                     \r\n</p>\r\n<p>\r\nEr trommelt schon manchen<br>\r\nins Blut und in sein Grab,<br>\r\nund dennoch liebt ein jeder,<br>\r\nden frohen Trommelknab.<br>                   \r\n</p>\r\n<p>\r\nVielleicht bin ich es morgen,<br>\r\nder sterben muß im Blut,<br>\r\nder Knab weiß nicht, wie Liebn,<br>\r\nweiß nicht wie Sterben tut.<br>                       \r\n</p>\r\n'),
(12, 'Hohe Tannen weisen die Sterne', 'Hohe Tannen weisen die Sterne (1923)', 'hohetannenweisendiesterne', '<p>\r\nHohe Tannen weisen die Sterne<br>\r\nAn der Iser in schäumender Flut.<br>\r\nLiegt die Heimat auch in weiter Ferne,<br>\r\nDoch du, Rübezahl, hütest sie gut.   <br>                     \r\n</p>\r\n<p>\r\nViele Jahre sind schon vergangen<br>\r\nUnd ich sehn’ mich nach Hause zurück<br>\r\nWo die frohen Lieder oft erklangen<br>\r\nDa erlebt’ ich der Jugendzeit Glück.<br>\r\n</p>\r\n<p>\r\nWo die Tannen steh’n auf den Bergen<br>\r\nWild vom Sturmwind umbraust in der Nacht<br>\r\nHält der Rübezahl mit seinen Zwergen<br>\r\nAlle Zeiten für uns treue Wacht.<br>\r\n</p>\r\n<p>\r\nHast dich uns auch zu eigen gegeben,<br>\r\nDer die Sagen und Märchen erspinnt,<br>\r\nUnd im tiefsten Waldesfrieden,<br>\r\nDie Gestalt eines Riesen annimmt.<br>\r\n</p>\r\n<p>\r\nKomm zu uns an das lodernde Feuer,<br>\r\nAn die Berge bei stürmischer Nacht.<br>\r\nSchütz die Zelte, die Heimat, die teure,<br>\r\nKomm und halte bei uns treu die Wacht.<br>\r\n</p>\r\n<p>\r\nHöre, Rübezahl, laß dir sagen,<br>\r\nVolk und Heimat sind nimmermehr frei.<br>\r\nSchwing die Keule wie in alten Tagen,<br>\r\nSchlage Hader und Zwietracht entzwei.  <br>                      \r\n</p>\r\n'),
(13, 'Nichts kann uns rauben', 'Nichts kann uns rauben, Karl Bröger (1923)', 'nichtskannunsrauben', '<p>\r\nNichts kann uns rauben<br>\r\nLiebe und Glauben<br>\r\nZu unserm Land<br>\r\nEs zu erhalten<br>\r\nUnd zu Gestalten<br>\r\nSind wir gesandt<br>\r\n</p>\r\n<p>\r\nMögen wir sterben<br>\r\nUnseren Erben<br>\r\nGilt dann die Pflicht<br>\r\nEs zu erhalten<br>\r\nUnd zu gestalten<br>\r\nDeutschland stirbt nicht! <br>                      \r\n</p>\r\n'),
(14, 'Alle Tag ist kein Sonntag', 'Alle Tag ist kein Sonntag (1924)', 'alletagistkeinsonntag', '<p>\r\nAlle Tage ist kein Sonntag<br>\r\nAlle Tag gibts keinen Wein<br>\r\nAber du sollst alle Tage<br>\r\nRecht lieb zu mir sein<br>\r\nUnd wenn ich einst tot bin<br>\r\nsollst du denken an mich<br>\r\nAuch am Abend eh du einschläfst<br>\r\nAber weinen darfst du nicht<br>\r\n</p>\r\n<p>\r\nAlle Tag flicht keine Rosen<br>\r\ndir das Leben in das Haar<br>\r\nund die goldenen Stunden<br>\r\nsind selten im Jahr<br>\r\nUnd gehts nicht nach Wunsch dir<br>\r\ndenk manch sonniges Glück<br>\r\nbringt nach Winter und Stürmen<br>\r\nder Lenz oft zurück<br>\r\n</p>\r\n<p>\r\nAlle Tag scheint nicht die Sonne<br>\r\nvom blauen Himmelszelt<br>\r\nund es kann nicht immer Mai sein<br>\r\nins unserer Welt<br>\r\nUnd wenn ich auch fern bin<br>\r\ndarfst du nicht traurig sein<br>\r\nmeines Lebens liebe Sonne<br>\r\nbleibst du nur allein<br>\r\n</p>\r\n'),
(15, 'Wir haben Hunger Hunger Hunger', 'Wir haben Hunger Hunger Hunger (1925)', 'wirhabenhungerhungerhunger', '<p>\r\nWir haben Hunger Hunger Hunger<br>\r\nhaben Hunger Hunger Hunger<br>\r\nhaben Hunger Hunger Hunger<br>\r\nhaben Durst<br>\r\n</p>\r\n<p>\r\nWo bleibt das Essen Essen Essen<br>\r\nbleibt das Essen Essen Essen<br>\r\nbleibt das Essen Essen Essen<br>\r\nbleibt die Wurst<br>\r\n</p>\r\n<p>\r\nWenn wir nichts kriegen kriegen kriegen<br>\r\nfangen wir Fliegen Fliegen Fliegen<br>\r\nfangen wir Fliegen Fliegen Fliegen<br>\r\nvon der Wand<br>\r\n</p>\r\n'),
(16, 'Im Wald im grünen Walde', 'Im Wald im grünen Walde (1927)', 'imwaldimgruenenwalde', '<p>\r\nIm Wald im grünen Walde<br>\r\nDa steht ein Försterhaus<br>\r\nDa schauet jeden Morgen<br>\r\nSo frisch und frei von Sorgen<br>\r\ndes Försters Töchterlein heraus<br>\r\nTaralala, taralala<br>\r\nDes Försters Töchterlein ganz frisch heraus<br>\r\nTaralala, taralala<br>\r\nDes Försters Töchterlein heraus<br>\r\n</p>\r\n<p>\r\nLore, Lore, Lore, Lore,<br>\r\nSchön sind die Mädchen<br>\r\nVon siebzehn, achtzehn Jahr.<br>\r\nLore, Lore, Lore, Lore,<br>\r\nSchöne Mädchen gibt es überall<br>\r\nUnd kommt der Frühling in das Tal,<br>\r\nGrüß mir die Lore noch einmal<br>\r\nade, ade, ade.<br>\r\n</p>\r\n<p>\r\nDer Förster und die Tochter,<br>\r\nDie schossen beide gut.<br>\r\nDer Förster schoß das Hirschlein,<br>\r\nDie Tochter traf das Bürschlein<br>\r\nTief in das junge Herz hinein.<br>\r\n</p>\r\n<p>\r\nSteh ich auf Bergeshöhen,<br>\r\nSchau über Täler hin<br>\r\nDann sehe ich so gerne<br>\r\nAus weiter Ferne,<br>\r\nDas Haus der jungen Försterin.<br>\r\n</p>\r\n'),
(17, 'Das ist nun gar verschollen', 'Das ist nun gar verschollen (1930)', 'dasistnungarverschollen', '<p>\r\nDas ist nun gar verschollen<br>\r\nwie ein romantisch Lied,<br>\r\nwas einst im jungen vollen<br>\r\nHerzen so heiß geglüht.<br>\r\nMich faßte das ernste Leben<br>\r\nmit seinem kalten Arm,<br>\r\ndes mochten wohl verschweben<br>\r\ndie Träume süß und warm<br>\r\n</p>\r\n<p>\r\nDas war ein Wiegen und Wanken<br>\r\nim zarten Minnespiel.<br>\r\nso ohne alle Gedanken<br>\r\nein namenlos Gefühl;<br>\r\nein Jubeln und ein Klagen<br>\r\nein Lieben immerdar,<br>\r\nein Geben und Versagen:<br>\r\nGott weiß es, was es war.<br>\r\n</p>\r\n<p>\r\nDabei ein frisches Ringen<br>\r\nin Liebe und in Haß:<br>\r\nbald mit den scharfen Klingen<br>\r\nbald mit dem vollen Glas;<br>\r\nund um den kühnen Busen<br>\r\nein hellblau-weißes Band,<br>\r\ndas wohl dem Sohn der Musen<br>\r\nrecht heldenmäßig stand<br>\r\n</p>\r\n<p>\r\nDas alles ist nun verschollen<br>\r\nwie ein romantisch Lied,<br>\r\nwas einst den jungen vollen<br>\r\nBusen so heiß durchglüht.<br>\r\nMich faßte das ernste Leben<br>\r\nmit seinem kalten Arm,<br>\r\ndes mochten wohl verschweben<br>\r\ndie Träume süß und warm<br>\r\n</p>\r\n'),
(18, 'Hunger Hunger Marmelade Karbonade', 'Hunger Hunger Marmelade Karbonade (1930)', 'hungerhungermarmeladekarbonade', '<p>\r\nHunger Hunger Hunger Hunger<br>\r\nMarmelade, Karbonade<br>\r\nEisbein Schnitzel Blumenkohl Salat<br>\r\n</p>\r\n<p>\r\nHunger Hunger Hunger Hunger<br>\r\nMarmelade, Karbonade<br>\r\nEisbein Schnitzel Blumenkohl Salat<br>\r\n</p>\r\n<p>\r\nO Erdbeertorte O Kuchenkrümel<br>\r\no warme Würstchen mit Salat<br>\r\n</p>\r\n<p>\r\nO Erdbeertorte O Kuchenkrümel<br>\r\nBratkartoffel<br>\r\nWir haben Hunger, Hunger Hunger Hunger ...<br>\r\n</p>\r\n');

